import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';

interface TodaySummaryCardProps {
  completedMissions: number;
  totalMissions: number;
  completionRate: number;
}

export function TodaySummaryCard({ completedMissions, totalMissions, completionRate }: TodaySummaryCardProps) {
  const percent = Math.round(completionRate * 100);
  return (
    <Card>
      <CardHeader className='pb-2'>
        <CardTitle className='text-base'>오늘의 달성률</CardTitle>
      </CardHeader>
      <CardContent className='space-y-3'>
        <div className='flex items-baseline gap-1'>
          <span className='text-4xl font-bold text-brand-primary'>{percent}</span>
          <span className='text-lg text-muted-foreground'>%</span>
        </div>
        <Progress value={percent} className='h-2' />
        <p className='text-sm text-muted-foreground'>{completedMissions}/{totalMissions} 미션 완료</p>
      </CardContent>
    </Card>
  );
}

