'use client';
import { MissionCard } from '@/components/common/mission-card';
import type { MissionStatus } from '@/lib/types/database';

interface MissionSectionProps {
  missions: MissionStatus[];
  onVerify: (missionId: string) => void;
}

export function MissionSection({ missions, onVerify }: MissionSectionProps) {
  if (missions.length === 0) return null;
  return (
    <section className='space-y-3'>
      <h2 className='text-lg font-semibold'>오늘의 미션</h2>
      <div className='flex gap-3 overflow-x-auto pb-2 -mx-4 px-4 scrollbar-hide'>
        {missions.map((mission) => (
          <MissionCard
            key={mission.id}
            mission={mission}
            isCompletedToday={mission.is_completed_today}
            onVerify={() => onVerify(mission.id)}
            className='w-[280px] shrink-0'
          />
        ))}
      </div>
    </section>
  );
}

