pseudocode:
import { EmptyGroupState, DashboardContent } from './_components';
import {
  getCurrentUserId,
  getMockGroupsForUser,
  getMockMissionStatuses,
  getMockTodayStatus,
  MOCK_VERIFICATION_FEEDS,
  MOCK_USER_STREAKS,
} from '@/lib/mocks';

export default function DashboardPage() {
  const userId = getCurrentUserId();
  const userGroups = getMockGroupsForUser(userId);

  // 그룹이 없으면 빈 상태 표시
  if (userGroups.length === 0) {
    return <EmptyGroupState />;
  }

  // 대시보드 데이터 준비
  const missions = getMockMissionStatuses(userId);
  const todayStatus = getMockTodayStatus(userId);
  const feeds = MOCK_VERIFICATION_FEEDS.slice(0, 10);
  const streak = MOCK_USER_STREAKS[userId] || { currentStreak: 0 };

  return (
    <DashboardContent
      missions={missions}
      todayStatus={todayStatus}
      feeds={feeds}
      streak={streak}
      userId={userId}
    />
  );
}
